create table movie
(
    budget             int            null,
    homepage           text           null,
    id                 int            not null
        primary key,
    original_language  text           null,
    original_title     text           null,
    overview           text           null,
    popularity         double         null,
    studio             text           null,
    country            text           null,
    release_date       text           null,
    revenue            int            null,
    runtime            int            null,
    status             text           null,
    tagline            text           null,
    title              text           null,
    vote_average       double         null,
    vote_count         int            null,
    picture_url        varchar(255)   null,
    quantity_available bigint         not null,
    stock_price        decimal(19, 2) null
);

create index FK_movies_idx
    on movie (id);

